import React from 'react';
import { Redirect } from 'react-router-dom'; // Import Redirect (or use Navigate in v6)

interface PrivateRouteProps {
  component: React.ComponentType<any>; // Make sure this matches the type of the component
}

const PrivateRoute = ({ component: Component }: PrivateRouteProps) => {
  const { user } = useAuth(); // Assuming useAuth gives you user state

  // If the user is not authenticated, redirect to the login page (or another route)
  if (!user) {
    return <Navigate to="/user" replace />;
  }

  // If the user is authenticated, render the component
  return <Component />;
};

export default PrivateRoute;
