import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Counter from './counter';
import UserForm from './UserForm';
import RichTextEditor from './RichTextEditor';
import './App.css';

function App() {
  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>Hello, Welcome</h1>
          <p>Please find the assignment task, select or press each button to know more.</p>

          {/* Navigation Links */}
          <nav>
            <Link to="/counter"><button className="btn">Counter Component</button></Link>
            <Link to="/user-form"><button className="btn">User Data Form</button></Link>
            <Link to="/rich-text"><button className="btn">Rich Text Editor</button></Link>
          </nav>
        </header>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<h2>Home Page</h2>} />
          <Route path="/counter" element={<Counter />} />
          <Route path="/user-form" element={<UserForm />} />
          <Route path="/rich-text" element={<RichTextEditor />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
