import React, { useState } from 'react';
import { Button, Box } from '@mui/material';

const Counter: React.FC = () => {
  const [count, setCount] = useState<number>(0);
  const [bgLevel, setBgLevel] = useState<number>(0);

  const increment = () => {
    setCount(count + 1);
    setBgLevel((prev) => Math.min(prev + 10, 100)); // Ensure bgLevel stays between 0 and 100
  };

  const decrement = () => {
    setCount(count - 1);
    setBgLevel((prev) => Math.max(prev - 10, 0)); // Ensure bgLevel stays between 0 and 100
  };

  const reset = () => {
    setCount(0);
    setBgLevel(0);
  };

  return (
    <Box sx={{ padding: 2 }}>
      <div
        className="counter-container"
        style={{
          background: `linear-gradient(to top, rgba(255, 0, 0, ${bgLevel / 100}), rgba(0, 0, 255, ${bgLevel / 100}))`,
        }}
      >
        <h2>Counter: {count}</h2>
        <Button variant="contained" onClick={increment}>Increment</Button>
        <Button variant="contained" onClick={decrement}>Decrement</Button>
        <Button variant="contained" onClick={reset}>Reset</Button>
      </div>
    </Box>
  );
};

export default Counter;
